import React from "react";

const fallbackImages = {
  Appliances:
    "https://www.defy.co.za/content/dam/defy-rainbow-za-aem/defy-rainbow-za-aemProductCatalog/product-images/8130220200-DTT-180/8130220200-MDM2-LOW-1.png/jcr:content/renditions/original`",
  Audio:
    "https://images.unsplash.com/photo-1518449002612-6d67e7d62d86?auto=format&fit=crop&w=800&q=80",
  Computers:
    "https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=800&q=80",
  Electronics:
    "https://images.unsplash.com/photo-1510557880182-3d4d3cba35a5?auto=format&fit=crop&w=800&q=80",
  Gaming:
    "https://images.unsplash.com/photo-1606813907291-6a5a1f7a85bb?auto=format&fit=crop&w=800&q=80",
  Gardening:
    "https://image.made-in-china.com/202f0j00NHbUemLGLMcv/2000W-45L-Electric-Lawnmower-High-Performance-Grass-Cutting-Machine.jpg",
  Groceries:
    "https://assets.hyperpure.com/data/images/products/d53961065493948f48763257607261b7.png",
  "Home and Kitchen":
    "https://images.unsplash.com/photo-1615874959474-d609969a95f8?auto=format&fit=crop&w=800&q=80",
  Paint:
    "data:image/jpeg;base64,```",
  Peripherals:
    "data:image/jpeg;base64,",
  Tools:
    "data:image/jpeg;base64,`",
  Toys:
    "data:image/jpeg;base64,",
  default:
    "https://via.placeholder.com/300?text=No+Image",
};

const ProductCard = ({ product }) => {
  if (!product) return null;

  // choose fallback per category
  const fallback =
    fallbackImages[product.category] || fallbackImages["default"];

  return (
    <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 p-4 break-inside-avoid">
      <img
        src={product.image_url || fallback}
        alt={product.name}
        className="w-full h-48 object-cover rounded-lg mb-3 bg-gray-50"
        onError={(e) => (e.target.src = fallback)}
      />

      <h2 className="text-lg font-semibold">{product.name}</h2>

      <p className="text-xl font-bold text-green-600 mt-1">R {product.price}</p>

      <p className="text-sm text-gray-500 mt-1">{product.retailer}</p>

      <a
        href={product.affiliate_link}
        target="_blank"
        rel="noopener noreferrer"
        className="block mt-4 w-full text-center bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded-lg transition"
      >
        View Deal
      </a>
    </div>
  );
};

export default ProductCard;
